import { Component, OnInit } from '@angular/core';
import { DjangoservicesService } from '../djangoservices.service';
import { Router } from '@angular/router';
import { MatDialog, MatDialogConfig } from '@angular/material/dialog';
import { MatSnackBar } from '@angular/material/snack-bar';
import { AddbottleComponent } from '../addbottle/addbottle.component';
import { UpdatebotlleComponent } from '../updatebotlle/updatebotlle.component';
import { DeletebottleComponent } from '../deletebottle/deletebottle.component';

@Component({
  selector: 'app-listbottles',
  templateUrl: './listbottles.component.html',
  styleUrls: ['./listbottles.component.css']
})
export class ListbottlesComponent implements OnInit {


  constructor(private es:DjangoservicesService,private router:Router ,public dialog: MatDialog,private snack:MatSnackBar) { }
bottles:any
  ngOnInit(): void {
    this.getBottles()
  }
  getBottles(){
    this.es.GetAllBottles().subscribe 
    ((data)=>{
      console.log(data)

    this.bottles=data.Bouteilles;
    },
    (error)=>{
    console.log(error);
    })
  }


  openDialog(){
    this.dialog.open(AddbottleComponent,{
  
      width:'40%'});
  }
  deleteBottle(id:number)
  {
    
    const dialogRef=this.dialog.open(DeletebottleComponent,{

      width:'390 px',panelClass:'confirm-dialog-container',
      disableClose:true, data:{
        message: 'Etes-vous sur de supprimer cette RVM?',
        buttonText: {
          ok: 'Oui',
          cancel: 'Non'
    }
  }
 
 
      });
    
 
      dialogRef.afterClosed().subscribe((confirmed: boolean) => {
        if (confirmed) {
        
          this.es.DeleteBottles(id).subscribe(
            (data)=>{
            
              console.log(data);
             
       
            },
            (error)=>{
             console.log(error)
           }
          );
   
       
       this.router.navigate(['/accueil/bottles'])
   .then(() => {
    window.location.reload();
    });
     }
     });
}
updateBottle(id:number)
  {let dialogconfig=new MatDialogConfig();
    dialogconfig.disableClose=true;
    dialogconfig.autoFocus=true;
    dialogconfig.data=id;
    dialogconfig.width="40%";
    
    const dialogRef=this.dialog.open(UpdatebotlleComponent,dialogconfig);

  }
  getImageUrl(type: string): string {
    if (type.includes( 'Coca') )
      return 'data:image/png;base64,';
      if (type.includes( 'Boga') )
      return 'data:image/jpeg;base64,';
      if (type.includes( 'Safia') )

    // Retourner une URL par défaut si le type ne correspond à aucune condition
    return 'data:image/jpeg;base64,';
    if (type.includes('Fanta'))
    return 'https://logowik.com/content/uploads/images/248_fanta_logo.jpg'
    if (type.includes('Sprite'))
    return 'data:image/jpeg;base64,'
    if (type.includes('Melliti'))
    return 'data:image/jpeg;base64,'
    if (type.includes('Marwa'))
    return 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRh5Br8ckkTpLgPKALBCQhaeK1rjjl6OHXN3MqTjL9aeag3W9_xNLe1Dplz4FjhDo4kY0A&usqp=CAU'
    if (type.includes('Fourat'))
    return'https://www.elka-consulting.com/wp-content/uploads/2015/04/fourat.jpg'

  }

}
